import React from 'react'
import styled from 'styled-components'
import Fade from 'react-reveal/Fade'

function Section() {
    return (
        <Wrap>
            <video autoPlay={true} loop={true} playsInline={true} muted>
                <source src="/videos/site.mp4" />
            </video>
            <Fade bottom>
                <ItemText>
                    <h1>Title</h1>
                    <p>Desctiption</p>
                </ItemText>
            </Fade>
            <Fade bottom>
                <Buttons>
                    <ButtonGroup>
                        <LeftButton>
                            Donate
                        </LeftButton>
                        <RightButton>
                            Learn More
                        </RightButton>
                    </ButtonGroup>
                    <DownArrow src='https://sukrins.github.io/tesla-clone/images/down-arrow.svg' />
                </Buttons>
            </Fade>
        </Wrap>
    )
}

export default Section

const Wrap = styled.div`
width: 100vw;
height: 100vh;
background-size: cover;
background-position: center;
background-repeat: no-repeat;
background-image: url('https://sukrins.github.io/tesla-clone/images/model-6.jpg');
display: flex;
flex-direction: column;
justify-content: space-between;
align-items: center;

video{
    object-fit: fill;
    max-height: 100vh;
    position: absolute;
    top: 0px;
    filter: brightness(50%);
    z-index: 0;
}
`

const ItemText = styled.div`
padding-top: 15vh;
text-align: center;
`

const Buttons = styled.div``

const ButtonGroup = styled.div`
display: flex;
margin-bottom: 30px;
@media (max-width: 768px){
    flex-direction: column;
}
`

const LeftButton = styled.div`
background-color: rgba(23, 26, 32, 0.8);
height: 40px;
width: 256px;
color: white;
display: flex;
justify-content: center;
align-items: center;
border-radius: 100px;
opacity: 0.85;
text-transform: uppercase;
font-size: 12px;
cursor: pointer;
margin: 8px;

`

const RightButton = styled(LeftButton)`
background: white;
opacity: 0.65;
color: black;
`

const DownArrow = styled.img`
margin-top: 20px;
height: 40px;
cursor: pointer;
overflow-x: hidden;
animation: animateDown infinite 1.5s;
`
