import React, { useState } from 'react'
import styled from 'styled-components'

function Header() {
    const [burgerStatus, setBurgerStatus] = useState(false);

    console.log('burgerStatus: ', burgerStatus);

    return (
        <Navbar show={burgerStatus}>
            <ul>
                <li><a href='#'>Home</a></li>
                <li><a href='#'>Works</a></li>
                <li><a href='#'>About</a></li>
                <li><a href='#'>Contact</a></li>
            </ul>
            <ButtonGroup onClick={() => setBurgerStatus(!burgerStatus)}>
                <LineOne show={burgerStatus}/>
                <LineTwo show={burgerStatus}/>
            </ButtonGroup>
        </Navbar>
    )
}

export default Header

const Navbar = styled.nav`
    min-height: 60px;   
    background-color: #fff;
    width: 350px;
    display: flex;
    align-items: center;
    justify-content: center
    transform: ${props => props.show ? 'translateX(0)' : 'translateX(100%)'};
    transition: width 0.6s linear;

    ul {
        display: flex;
        list-style-type: none;
        padding: 0;
        margin: 0;

        li {
            transform: ${props => props.show ? 'translateY(360deg)' : 'translateY(0)'};
            opacity: ${props => props.show ? 1 : 0};

            a {
                position: relative;
                color: #000;
                text-decoration: none;
                margin: 0 10px;
            }
        }
    }
`
const ButtonGroup = styled.button`
    background-color: #fff;
    border: 0;
    cursor: pointer;
    padding: 0;
    position: relative;
    height: 30px;
    width: 30px;

    &:focus {
        outline: 0;
    }
`
const LineOne = styled.div`
    background-color: #5290f9;
    height: 2px;
    position: absolute;
    width: 20px;
    top: 10px;
    left: 5px;
    transform: ${props => props.show ?
         'rotate(-765deg) translateY(5.5px)'
          : ''};
    transition: transform 0.7s linear;
`
const LineTwo = styled(LineOne)`
    top: auto;
    bottom: 10px;
    transform: ${props => props.show ?
        'rotate(765deg) translateY(-5.5px)'
         : ''};
`
